    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        
        Book B = new Book();
        B.setISBN(jTextField1.getText());
        B.setTitle(jTextField2.getText());
        B.setAuthor(jTextField3.getText());
        B.setSubject(jTextField4.getText());
        B.setPublisher(jTextField5.getText());
        B.setPublicationYear(Integer.parseInt(jTextField6.getText()));
        B.setPrice(Double.parseDouble(jTextField8.getText()));
        
        String A = B.updateBook(con);
        jTextField9.setText(A);
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        Book B = new Book();
        
        B.setISBN(jTextField1.getText());
       
        String A = B.findBook(con);
        
        jTextField1.setText(B.getISBN());
        jTextField2.setText(B.getTitle());
        jTextField3.setText(B.getAuthor());
        jTextField4.setText(B.getSubject());
        jTextField5.setText(B.getPublisher());
        jTextField6.setText(Integer.toString(B.getPublicationYear()));
        jTextField8.setText(Double.toString(B.getPrice()));
        
        jTextField9.setText(A);
    }//GEN-LAST:event_jButton1ActionPerformed

