    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        Book B = new Book();
        
        B.setISBN(jTextField1.getText());
       
        String A = B.deleteBook(con);
        
        jTextField1.setText("");
        jTextField2.setText("");
        jTextField3.setText("");
        jTextField4.setText("");
        jTextField5.setText("");
        jTextField6.setText("");
        jTextField8.setText("");
        
        jTextField9.setText(A);
    }//GEN-LAST:event_jButton3ActionPerformed

